import React, { useState, useEffect, useRef } from "react";
import API from "./api";

export default function Chat() {
  const [input, setInput] = useState("");
  const [conversation, setConversation] = useState([]);
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { sender: "user", text: input };
    setConversation(prev => [...prev, userMessage]);
    setInput("");
    setLoading(true);

    try {
      const response = await API.post("/chat", {
        message: input,
        conversation_id: "abc123", // Optional, or generate on frontend/backend
      });

      const aiMessage = {
        sender: "ai",
        text: response.data.response || "No response received.",
      };

      setConversation(prev => [...prev, aiMessage]);
    } catch (err) {
      setConversation(prev => [
        ...prev,
        { sender: "ai", text: "Error: " + err.message },
      ]);
    }

    setLoading(false);
  };

  const handleKeyDown = e => {
    if (e.key === "Enter") sendMessage();
  };

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [conversation]);

  return (
    <div style={{ padding: "2rem", maxWidth: "600px", margin: "0 auto" }}>
      <h2>ðŸ’¬ Chat with AI</h2>

      <div style={{ border: "1px solid #ccc", padding: "1rem", height: "400px", overflowY: "auto" }}>
        {conversation.map((msg, index) => (
          <div key={index} style={{ marginBottom: "1rem", textAlign: msg.sender === "user" ? "right" : "left" }}>
            <b>{msg.sender === "user" ? "You" : "AI"}:</b> {msg.text}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <input
        type="text"
        value={input}
        onChange={e => setInput(e.target.value)}
        onKeyDown={handleKeyDown}
        placeholder="Type a message"
        style={{ width: "80%", padding: "0.5rem", marginTop: "1rem" }}
      />
      <button onClick={sendMessage} disabled={loading} style={{ padding: "0.5rem", marginLeft: "1rem" }}>
        {loading ? "Sending..." : "Send"}
      </button>
    </div>
  );
}
